<?php    require_once("sqlfunctions.php");    $input = strtoupper( $_GET['input'] );    $input =  mysql_real_escape_string($input);    //$sql = "SELECT a.code FROM airports a WHERE a.code like '".$input."%'";    //$sql = "SELECT * FROM airports a WHERE a.code like '".$input."%' limit 0, 7";    $sql = "SELECT * FROM airports a WHERE a.code like '".$input."%'";    $result = sql_result($sql);    $index = 0;    $airportCodes = array();     while($airport = sql_fetch_obj($result))    {	$airportCodes[$index] = $airport->city.", ";        if($airport->state != null)        {            $airportCodes[$index] = $airportCodes[$index].$airport->state." - ".$airport->name." (".$airport->code.")";        }        else        {            $sql = "SELECT * FROM country c WHERE c.code = '".$airport->country."'";            $result = sql_result($sql);            $country = sql_fetch_obj($result);            $airportCodes[$index] = $airportCodes[$index].$country->name." - ".$airport->name." (".$airport->code.")";        }	$index++;    }        //$sql = "SELECT * FROM airports a WHERE a.name like '".$input."%' limit 0, 7";    $sql = "SELECT * FROM airports a WHERE a.name like '".$input."%' AND a.code <> '".$input."'";    $result = sql_result($sql);    while ($airport = sql_fetch_obj($result))    {	$airportCodes[$index] = $airport->city.", ";        if($airport->state != null)        {            $airportCodes[$index] = $airportCodes[$index].$airport->state." - ".$airport->name." (".$airport->code.")";        }        else        {            $sql = "SELECT * FROM country c WHERE c.code = '".$airport->country."'";            $result = sql_result($sql);            $country = sql_fetch_obj($result);            $airportCodes[$index] = $airportCodes[$index].$country->name." - ".$airport->name." (".$airport->code.")";        }	$index++;    }        header("Content-Type: text/xml");    echo "<?xml version=\"1.0\" encoding=\"utf-8\" ?>          <results>";    for ($i=0;$i<count($airportCodes);$i++)    {            echo "<rs>".$airportCodes[$i]."</rs>";    }    echo "</results>";?>