<?php    require_once("sqlfunctions.php");    $input = strtoupper( $_GET['input'] );    $input =  mysql_real_escape_string($input);    //$sql = "SELECT a.code FROM airports a WHERE a.code like '".$input."%'";	$sql = "SELECT * FROM airports a WHERE a.code like '".$input."%' limit 0, 4";    $result = sql_result($sql);    $index = 0;    $airportCodes = array();    while ($dest = sql_fetch_obj($result))    {	$airportCodes[$index] = $dest->city." - ".$dest->name." (".$dest->code.")";	$index++;    }     	$sql = "SELECT * FROM airports a WHERE a.name like '".$input."%' limit 0, 4";    $result = sql_result($sql);    while ($dest = sql_fetch_obj($result))    {	$airportCodes[$index] = $dest->city." - ".$dest->name." (".$dest->code.")";	$index++;    }	     header("Content-Type: text/xml");    echo "<?xml version=\"1.0\" encoding=\"utf-8\" ?>          <results>";    for ($i=0;$i<count($airportCodes);$i++)    {            echo "<rs>".$airportCodes[$i]."</rs>";    }    echo "</results>";?>